            collection.at(19).should.equal(collection.get(898));
            collection.pluck("name").join(",").should.equal('Single Series,Jumbo Comics,Movie Comics,Superman,Magic Comics,Four Color,Large Feature Comics,New Heroic Comics,Batman,Future Comics,Planet Comics,The Human Torch,Super-Mystery Comics,Master Comics,Whiz Comics,Sparkler Comics,Rangers of Freedom Comics,Daredevil Comics,Four Favorites,Captain Marvel Adventures');
